import styled from 'styled-components';

export const StyledSeasonAverage = styled.div`
  // border: 1px solid grey;
  text-align: center;
  margin-bottom: 2%;

  & h3, h4, p {
    margin: 0;
  }
  
  & p {
    font-weight: bold;
    font-size: 1.5em;
    margin-bottom: 5%;
  }
  
  & h4 {
    margin-top: -5%;
  }
`;
